post.description:
Sample request:

    List Jobs that match a description
            
    {
             "description": "Lowry Mill",
             "pagination": {
                "page": 1,
                "rowCount": 10
                }
     }
            
            
        
    List Jobs that are at a none closed job status - order by estimatedStartDateTimeFrom
            
    {
             "jobStatusId": [
               1,2,3,5
             ],
             "orderBy": [
                            {
                                "propertyName": "estimatedStartDateTimeFrom",
                                "descending": true
                            }
             ],
             "pagination": {
                "page": 1,
                "rowCount": 500
                }
     }
